import { Component, Inject, OnInit } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms'
import { Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr'
import { AuthService } from '../service/auth.service';
import { MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';

@Component({
  selector: 'app-updatepopup',
  templateUrl: './updatepopup.component.html',
  styleUrls: ['./updatepopup.component.css']
})
export class UpdatepopupComponent implements OnInit {

  constructor(private builder: FormBuilder, private service: AuthService,
     @Inject(MAT_DIALOG_DATA) public data: any,private toastr:ToastrService,private dialog: MatDialogRef<UpdatepopupComponent>,) {

    // this.service.getuserrole().subscribe(res => {
    //   this.rolelist = res;
    // });
  }
  editdata: any;
  ngOnInit(): void {
    this.service.GetAllRole().subscribe(res=>{
      this.rolelist=res;
    })
    if (this.data.usercode != '' && this.data.usercode != null) {
      this.service.Getbycode(this.data.usercode).subscribe(res=>{
        this.editdata=res;
        this.registerform.setValue({id:this.editdata.id,name:this.editdata.name,email:this.editdata.email,password:this.editdata.password,role:this.editdata.role,gender:this.editdata.gender,isactive:this.editdata.isactive})

      });
    }
  }
  rolelist: any;


  registerform = this.builder.group({
    id: this.builder.control(''),
    name: this.builder.control(''),
    password: this.builder.control(''),
    email: this.builder.control(''),
    gender: this.builder.control('male'),
    role: this.builder.control('', Validators.required),
    isactive: this.builder.control(false)
  });

  loaduserdata(code: any) {
    this.service.GetUserbyCode(code).subscribe(res => {
      this.editdata = res;
      console.log(this.editdata);
      this.registerform.setValue({
        id: this.editdata.id, name: this.editdata.name,
        password: this.editdata.password, email: this.editdata.email, gender: this.editdata.gender,
        role: this.editdata.role, isactive: this.editdata.isactive
      });
    });
  }
  UpdateUser() {
    // this.service.updateuser(this.registerform.value.id, this.registerform.value).subscribe(res => {
    //   this.toastr.success('Updated successfully.');
    //   this.dialogref.close();
    //});
    if(this.registerform.valid){
      this.service.Updateuser(this.registerform.value.id,this.registerform.value).subscribe(res=>{
      this.toastr.success('Updated Sucessfully');
      this.dialog.close();
      });

    }
    else{
      this.toastr.warning('Please Select Role.')
    }
  }

}
<h1 mat-dialog-title>Update User</h1>
<form [formGroup]="registerform" (ngSubmit)="UpdateUser()">
    <div mat-dialog-content>

        <mat-form-field>
            <mat-label>Role</mat-label>
            <mat-select formControlName="role">
                <mat-option value=''>Select Role</mat-option>
                        <mat-option *ngFor="let item of rolelist" [value]="item.code">
                    {{item.name}}
                </mat-option>
            </mat-select>
        </mat-form-field>

        <section class="example-section">
            <br>
            <mat-checkbox  formControlName="isactive" class="example-margin">Is Active</mat-checkbox>
        </section>

    </div>
    <div mat-dialog-actions>
        <button type="submit" mat-raised-button color="primary" cdkFocusInitial>Save</button>
        <button mat-raised-button color="warn" mat-dialog-close>Close</button>
    </div>
</form>
